REPLACE INTO script_texts (entry, content_default, `comment`) VALUES
(-1999981,  "Beware! We are attacked!", "SAY_WP_1"),
(-1999982,  "It must be the purity of the Mark of the Lightbringer that is drawing forth the Scourge to attack us. We must proceed with caution lest we be overwhelmed!", "SAY_WP_1"),
(-1999983,  "This land truly needs to be cleansed by the Light! Let us continue on to the tomb. It isn't far now...", "SAY_WP_2"),
(-1999984,  "Be welcome, friends!", "SAY_WP_3"),
(-1999985,  "Thank you for coming here in remembrance of me. Your efforts in recovering that symbol, while unnecessary, are certainly touching to an old man's heart.", "SAY_WP_4"),
(-1999986,  "Please, rise my friend. Keep the Blessing as a symbol of the strength of the Light and how heroes long gone might once again rise in each of us to inspire.", "SAY_WP_5"),
(-1999987,  "Thank you my friend for making this possible. This is a day that I shall never forget! I think I will stay a while. Please return to High Priestess MacDonnell at the camp. I know that she'll be keenly interested to know of what has transpired here.", "SAY_WP_6");